maintenance.scss:
* {
    box-sizing: border-box;
}

body {
    -webkit-align-items: center;
    align-items: center;
    background: #888 url("../img/coming-soon.jpg") no-repeat center center;
    background-size: cover;

    -ms-box-orient: horizontal;
    color: #798289;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-align: center;
    -ms-flex-pack: space-around;
    font-family: Helvetica, Arial, Sans-Serif;
    font-size: 0.8em;
    height: 100vh;

    -webkit-justify-content: space-around;
    justify-content: space-around;
    line-height: 1.5em;
    margin: 0;
    min-height: 480px;
    min-width: 320px;
    padding: 20px 30px;
}

main {
    background-color: #fff;
    box-shadow: 0 2px 0 0 rgba(0, 0, 0, 0.07);
    display: block;
    padding: 20px 30px;
    text-align: center;
}

section {
    margin-bottom: 40px;
}

fieldset {
    border: 0;
    padding: 0;
}

legend {
    border: 0;
    clip: rect(0 0 0 0);
    height: 1px;
    margin: -1px;
    overflow: hidden;
    padding: 0;
    position: absolute !important;
    width: 1px;
}

label,
input {
    display: block;
    height: 30px;
    line-height: 30px;
    width: 100%;
}

label {
    color: #313b47;
    font-size: 14px;
    font-weight: normal;
    text-align: center;
}

input {
    -webkit-appearance: none;
    border-radius: 2px;
    border-style: solid;
    width: 100%;
}

input[type=text] {
    background-color: #f7f8f9;
    border: 1px solid #e1e6eb;
    margin-bottom: 1rem;
    padding: 0 1em;
}

input[type=submit] {
    background: #00abc9;
    border: 0;
    color: #fff;
    cursor: pointer;
    font-size: 12px;
    padding: 0 15px;
}

a {
    color: #00b9d3;
    margin: 0 5px;
    text-decoration: none;
}

h1 {
    color: #3c4043;
    font-size: 24px;
    font-weight: normal;
    margin-top: 15px;
}

div p {
    margin-bottom: 0;
    margin-top: 0;
}

.form-field--error {
    margin-bottom: 1rem;
}

.form-field--error input[type=text] {
    margin-bottom: 0;
}

.form-field--error .form-inlineMessage {
    color: #ac0005;
    margin-top: 0.375rem;
    width: 100%;
}

.powered-by {
    color: #acb3be;
    font-size: 11px;
    font-style: italic;
    line-height: 24px;
}

.powered-by a {
    background: url("../img/bc_logo_sprite.png") 0 -75px no-repeat;
    display: inline-block;
    height: 22px;
    opacity: 0.7;
    text-indent: -9999px;
    width: 95px;
}

.powered-by a:hover {
    opacity: 1;
}
@media screen and (min-width: 540px) {
    body {
        font-size: 1em;
    }

    main {
        margin: 0 auto;
        padding: 45px 60px;
        width: 500px;
    }

    label,
    input {
        display: inline-block;
        margin-right: 10px;
        width: auto;
    }

    label {
        text-align: left;
    }

    input[type=text] {
        margin-bottom: 0;
        margin-right: 10px;
    }

    .form-field {
        display: inline-block;
        position: relative;
    }

    .form-field--error .form-inlineMessage {
        left: 100px;
        position: absolute;
        top: 30px;
    }
}

assets/scss/components/stencil/category/:
// =============================================================================
// CATEGORY (CSS)
// =============================================================================


// Category header
// -----------------------------------------------------------------------------

.category-header-image {
    width: 100%;
}
.catagory-space {
    margin-top: 20px!important;
}
// .catagory-space .row{
//     margin: 0;
// }
// .catagory-space #colm-left, .catagory-space .colright{
//     padding: 0;
// }
.custom-cat-img-sec{
    border-right: 1px solid stencilColor("custom-border-color");
    padding: 15px;
}
.custom-cat-img-sec div{
    padding: 0;
}

.category-top {
    border: 1px solid #eeeeee;
    padding: 15px;
    margin-bottom: 20px;
    margin-left: 0;
    margin-right: 0;
    border-radius: 8px;
}
.category-top .section-heading{
    margin: 0px;
    text-transform: none;
}
.category-top img{
    border-radius: 8px;
    padding: 0px;
}
.cate-border {
    border: 1px solid #eee;
    margin: 0;
    padding: 10px 0;
    border-radius: 8px;
    margin-bottom: 20px;
    align-items: center;
}
.actionBar .form-field{
    margin: 0 0 0rem;
}
.category-grid .active svg {
    fill: stencilColor("color-textBase--hover");
    cursor: pointer;
}
.cate-border span.active {
    border: 1px solid stencilColor("color-secondary-background");
    border-radius: 0px;
}
.cate-border span {
    padding: 1px 3px 3px 3px;
    cursor: pointer;
    border: 1px solid transparent;
    display: inline-block;
    margin-top: 2px;
    border-radius: 0;
}
.sun-cat-sin a {
    font-weight: normal;
    font-size: 16px;
    text-align: center;
    text-transform: none;
}
.cate-sub-name{
    text-align: center;
    margin-top: 10px;
    margin-bottom: 0;
}
.listItem-body .card-figcaption-button{
    background: stencilColor("color-white")!important;
    display: inline-block;
}
.listItem-body .card-figcaption-button:hover{
    background: stencilColor("color-textBase--hover")!important;
}
.listItem-body .card-figcaption-button:hover svg{
    fill: #fff!important;
    stroke: #fff;
}
.listItem-body .card-figcaption-button svg{
    stroke: stencilColor("color-textBase");
}
.catagory-space .page-sidebar {
    padding: 15px;
}
.listItem-content .card-rating {
    display: block;
    float: none;
    margin: 10px 0px;
}
.quickSearchResults .listItem-content .card-rating {
    display: none!important;
}

@media(min-width: 1200px) and (max-width: 1440px){
    #colm-left, .sing-brand .colm-left{
        -ms-flex: 0 0 20%;
        flex: 0 0 20%;
        max-width: 20%;
    }
    .colright{
        -ms-flex: 0 0 80%;
        flex: 0 0 80%;
        max-width: 80%;
    }
}
@media(min-width: 991px) and (max-width: 1199px){
    #colm-left, .sing-brand .colm-left{
        -ms-flex: 0 0 25%;
        flex: 0 0 25%;
        max-width: 25%;
    }
    .colright{
        -ms-flex: 0 0 75%;
        flex: 0 0 75%;
        max-width: 75%;
    }
}
@media(max-width: 800px){
    .category-top{
        padding: 15px 0;
    }
    .catedes{
        padding: 0px 15px 0px 15px;
    }
    .sun-cat-sin{
        padding-bottom: 10px;
    }
    .cate-sub-name{
        margin-bottom: 10px;
    }
    .xs-filter{
        background: stencilColor("custom-color");
        border-radius: 0px;
        border: none;
    }
    .catagory-space .page-sidebar {
        margin-bottom: 0px;
        padding: 0;
        border: none;
    }
    // #colm-left{
    //     display: none;
    // }
    .colright{
        -ms-flex: 0 0 100%;
        flex: 0 0 100%;
        max-width: 100%;
    }
}

@media(max-width: 575px){
    .catedes {
        padding: 0px 15px 0px 15px;
    }
    .sun-cat-sin a{
        font-size: 14px;
    }
    .productGrid .product {
        max-width: 50%;
        width: 50%!important;
    }
}
@media(max-width: 375px){
    .category-grid {
        padding: 0 0px!important;
    }
    .cate-border span {
        padding: 5px;
    }

}
@media(max-width: 320px){
    .category-grid {
        padding: 0 0px!important;
        max-width: 24%;
        flex: 0 0 24%;
    }
    .actionbar-filter{
        max-width: 76%;
        flex: 0 0 76%;
        padding: 0px 0px 0px;
    }
    .actionBar-section {
        float: right;
    }
    .cate-border .actionBar-section .form-label {
        padding-left: .7rem;
    }
    .actionBar-section .form-input, .actionBar-section .form-select {
        padding-right: 8px;
        padding-left: 5px;
    }
    .actionBar-section .form-input, .actionBar-section .form-select {
        float: right!important;
    }
}
@media (min-width: 991px) and (max-width: 1199px){
    .sun-cat-sin {
        -ms-flex: 0 0 30%;
        flex: 0 0 30%;
        max-width: 30%;
    }
}
